--- modus-wc-loader.stories.ts ---

import { Meta, StoryObj } from '@storybook/web-components';
import { html } from 'lit';
import { ifDefined } from 'lit/directives/if-defined.js';
import { LoaderColor, LoaderVariant } from './modus-wc-loader';
import { DaisySize } from '../types';

interface LoaderArgs {
  color: LoaderColor;
  'custom-class'?: string;
  size: DaisySize;
  variant: LoaderVariant;
}

const meta: Meta<LoaderArgs> = {
  title: 'Components/Loader',
  component: 'modus-wc-loader',
  args: {
    color: 'primary',
    'custom-class': '',
    size: 'md',
    variant: 'spinner',
  },
  argTypes: {
    color: {
      control: { type: 'select' },
      options: [
        'primary',
        'secondary',
        'accent',
        'neutral',
        'info',
        'success',
        'warning',
        'error',
      ],
    },
    size: {
      control: { type: 'select' },
      options: ['xs', 'sm', 'md', 'lg'],
    },
    variant: {
      control: { type: 'select' },
      options: ['ball', 'bars', 'dots', 'infinity', 'ring', 'spinner'],
    },
  },
};

export default meta;

type Story = StoryObj<LoaderArgs>;

export const Default: Story = {
  render: (args) => {
    return html`
      <modus-wc-loader
        aria-label="Loading spinner"
        color="${args.color}"
        custom-class="${ifDefined(args['custom-class'])}"
        size="${args.size}"
        variant="${args.variant}"
      ></modus-wc-loader>
    `;
  },
};

export const Ball: Story = {
  render: (args) => {
    return html`
      <modus-wc-loader
        aria-label="Loading ball"
        color="${args.color}"
        custom-class="${ifDefined(args['custom-class'])}"
        size="md"
        variant="ball"
      ></modus-wc-loader>
    `;
  },
};

export const Bars: Story = {
