export const Default: Story = {
  ...Template,
};

export const MultiSelect: Story = {
  render: (args) => {
    // Initialize args.items if empty
    if (!args.items || args.items.length === 0) {
      args.items = [...items];
    }

    const handleChipRemove = (e: CustomEvent<IAutocompleteItem>) => {
