export const BackgroundFigureImage: Story = {
  ...Template,
  // prettier-ignore
  render: (args) => html`
<modus-wc-card
  aria-label="Full image card"
  background-figure
  ?bordered=${args.bordered}
  custom-class=${ifDefined(args['custom-class'])}
  layout=${ifDefined(args.layout)}
  padding=${ifDefined(args.padding)}
>
