        },
      },
    },
    'input-aria-invalid': {
      control: { type: 'select' },
      options: ['true', 'false'],
    },
    options: {
      description: 'Array of option objects for the select dropdown',
      table: {
        type: {
          detail: `
            Interface: ISelectOption
