export const ShadowDomParent: Story = {
  render: (args) => {
    // Create a unique shadow host for chip component
    if (!customElements.get('chip-shadow-host')) {
      const ChipShadowHost = createShadowHostClass<ChipArgs>({
        componentTag: 'modus-wc-chip',
        propsMapper: (v: ChipArgs, el: HTMLElement) => {
          const chipEl = el as unknown as {
            ariaLabel: string;
            active: boolean;
            customClass: string;
            disabled: boolean;
            hasError: boolean;
            label: string;
            shape: string;
            showRemove: boolean;
            size: string;
            variant: string;
          };
          chipEl.ariaLabel = 'Click me chip';
          chipEl.active = Boolean(v.active);
          chipEl.shape = v.shape || 'rectangle';
          chipEl.size = v.size;
          chipEl.variant = v.variant;
          chipEl.customClass = v['custom-class'] || '';
          chipEl.disabled = Boolean(v.disabled);
          chipEl.hasError = Boolean(v['has-error']);
          chipEl.label = v.label;
          chipEl.showRemove = Boolean(v['show-remove']);
        },
      });
      customElements.define('chip-shadow-host', ChipShadowHost);
    }

    return html`<chip-shadow-host .props=${{ ...args }}></chip-shadow-host>`;
  },
};

export const Migration: Story = {
  parameters: {
    docs: {
      description: {
        story: `
#### Breaking Changes

  - \`chip-style\` prop has been renamed to \`variant\` and values changed from \`solid\` to \`filled\`.
  - \`closeClick\` event has been renamed to \`chipRemove\`.
  - \`show-close\` prop has been renamed to \`show-remove\`.
  - Size values have changed from verbose names (\`medium\`, \`small\`) to abbreviations (\`md\`, \`sm\`).

#### Prop Mapping

| 1.0 Prop       | 2.0 Prop    | Notes                                             |
|----------------|-------------|---------------------------------------------------|
| active         | active      |                                                   |
| advanced-chip  |             | Not carried over                                  |
| aria-label     | aria-label  |                                                   |
| chip-id        |             | Not carried over                                  |
| chip-style     | variant     | \`solid\` → \`filled\`, \`outline\` → \`outline\` |
| disabled       | disabled    |                                                   |
| has-error      | has-error   |                                                   |
| image-url      |             | Not carried over, use slot instead                |
| max-width      |             | Not carried over, use CSS instead                 |
| show-checkmark |             | Not carried over, use slot instead                |
| show-close     | show-remove |                                                   |
| size           | size        | \`medium\` → \`md\`, \`small\` → \`sm\`           |
| value          | label       |                                                   |
|                | shape       | New in 2.0: \`rectangle\` (default), \`circle\` |

#### Event Mapping

| 1.0 Event   | 2.0 Event   | Notes |
|-------------|-------------|-------|
| chipClick  | chipClick  |       |
| closeClick | chipRemove |       |
        `,
      },
    },
    controls: { disable: true },
    canvas: { disable: true },
  },
  render: () => html`<div></div>`,
};

